'BEGIN',
'    -- Declare variables to hold the ID_HANDSON, QTDE_INSCRITOS, and POSICOES',
'    FOR rec IN (',
'        SELECT ',
'            H.ID_HANDSON,',
'            COUNT(C.ID_CADASTRO) AS QTDE_INSCRITOS,',
'            H.POSICOES',
'        FROM HANDSON H',
'        LEFT JOIN CADASTRO C ',
