'create or replace PROCEDURE CREATE_APEX_USERS_BATCH',
'AS',
'    CURSOR cur_users IS',
'        SELECT email, PASSWORD',
'        FROM CADASTRO_USUARIOS',
'        WHERE CADASTRADO_APEX = ''N'';',
'',
'    l_workspace_id NUMBER;',
'    l_group_id     NUMBER;',
'BEGIN',
'    -- Get the security group ID for the workspace',
'    l_workspace_id := apex_util.find_security_group_id(''7517595396827368'');',
'    apex_util.set_security_group_id(l_workspace_id);',
'',
'    -- Get the group ID (if applicable)',
'    l_group_id := apex_util.get_group_id(NULL);',
'',
'    -- Iterate through users with CADASTRO_APEX = ''N''',
'    FOR rec IN cur_users LOOP',
'        BEGIN',
'            -- Create the APEX user',
'            apex_util.create_user(',
'                p_user_name      => rec.email,',
'                p_email_address  => rec.email,',
'                p_web_password   => rec.password,',
'                p_group_ids      => l_group_id,',
'                p_default_schema => NULL,',
'                p_change_password_on_first_use => ''N''',
'            );',
'',
'            -- Assign the role to the user',
'            APEX_ACL.ADD_USER_ROLE(',
'                p_application_id => 100,',
'                p_user_name      => rec.email,',
'                p_role_static_id => ''Cidadao''',
'            );',
'',
'            -- Update user record to mark as processed',
'            UPDATE CADASTRO_USUARIOS',
'            SET CADASTRADO_APEX = ''Y'', PASSWORD = ''''',
'            WHERE email = rec.email;',
'',
'            COMMIT;',
'        EXCEPTION',
'            WHEN OTHERS THEN',
'                -- Log error but continue processing next users',
'                DBMS_OUTPUT.PUT_LINE(''Error processing '' || rec.email || '': '' || SQLERRM);',
'                ROLLBACK;',
'        END;',
'    END LOOP;',
'END CREATE_APEX_USERS_BATCH;',
