'create or replace PROCEDURE update_geocode_info AS',
'BEGIN',
'    FOR rec IN (SELECT centrais_id, ENDERECO FROM centrais_atendimento) LOOP',
'        DECLARE',
'            v_json CLOB;',
'            v_latitude NUMBER;',
'            v_longitude NUMBER;',
'        BEGIN',
'            -- Retrieve the JSON response for each address',
unistr('            SELECT SDO_GCDR.ELOC_GEOCODE(rec.ENDERECO || '' - S\00E3o Paulo/SP'')'),
'            INTO v_json',
'            FROM dual;',
'            ',
'            -- Extract latitude and longitude using JSON functions',
'            SELECT JSON_VALUE(v_json, ''$[0].matches[0].y''),',
'                   JSON_VALUE(v_json, ''$[0].matches[0].x'')',
'            INTO v_latitude, v_longitude',
'            FROM dual;',
'            ',
'            -- Update the table with retrieved latitude and longitude',
'            UPDATE centrais_atendimento',
'            SET latitude = v_latitude, longitude = v_longitude',
'            WHERE centrais_id = rec.centrais_id;',
'        END;',
'    END LOOP;',
'    ',
'    COMMIT;',
'END update_geocode_info;',
